// src/components/HeroSection.jsx
import React from 'react';

const HeroSection = ({ title, subtitle }) => {
  return (
    <div className="relative w-full h-[400px] flex items-center justify-center overflow-hidden multicolor-border mb-4">
      {/* ðŸ”² Background Video */}
      <video
        autoPlay
        muted
        loop
        playsInline
        className="absolute w-full h-full object-cover z-0"
      >
        <source src="/videos/Nubian.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      {/* ðŸŸ§ Overlay Content */}
      <div className="relative z-10 text-center text-white p-6 bg-black bg-opacity-50 rounded-lg">
        {/* ðŸ”· Centered Logo */}
        <img
          src="https://raw.githubusercontent.com/MyAlkalineVeganJourney/HF/main/Images/MAVJDigitalLogo.JPG"
          alt="MAVJ Logo"
          className="mx-auto mb-4 w-64"
        />

        {/* ðŸ”¶ Page Titles */}
        <h1 className="text-4xl font-bold">{title}</h1>
        {subtitle && <h2 className="text-xl italic mt-2">{subtitle}</h2>}
      </div>
    </div>
  );
};

export default HeroSection;
