// src/components/PageLayout.jsx
import React from 'react';
import Navbar from './Navbar';
import HeroSection from './HeroSection';
import PhotoGallery from './PhotoGallery';
import Footer from './Footer';

const PageLayout = ({ title, subtitle, showHero = true, showFooter = true, showGallery = true, children }) => {
  return (
    <div className="page-layout">
      {/* ðŸŸ¦ Container 1: Navbar */}
      <Navbar />

      {/* ðŸŸ¨ Container 2: Hero */}
      {showHero && (
        <section className="hero-title multicolor-border">
          <h1>{title}</h1>
          {subtitle && <h3>{subtitle}</h3>}
        </section>
      )}

      {/* ðŸŸ« Container 3: Logo + Background Video */}
      <section className="logo-video-section multicolor-border">
        <video autoPlay loop muted className="background-video">
          <source src="/videos/background.mp4" type="video/mp4" />
        </video>
        <img src="/images/MAVJLogo.jpg" alt="MAVJ Logo" className="overlay-logo" />
      </section>

      {/* ðŸŸª Container 4: Paragraph */}
      <section className="page-paragraph multicolor-border">
        <p>Welcome to My Alkaline Vegan Journey. We align health, herbs, frequency, and ancient knowledge.</p>
      </section>

      {/* ðŸŸ§ Container 5: 6 Content Boxes */}
      <section className="content-boxes multicolor-border">
        {[...Array(6)].map((_, i) => (
          <div className="content-box" key={i}>
            <h3>Title {i + 1}</h3>
            <p>Short description here.</p>
          </div>
        ))}
      </section>

      {/* ðŸŸ¥ Container 6: Gallery */}
      {showGallery && <PhotoGallery />}

      {/* ðŸŸ© Container 7: Footer */}
      {showFooter && <Footer />}
    </div>
  );
};

export default PageLayout;
