import React from 'react';
import { useNavigate } from 'react-router-dom';
import HeroSection from '../components/HeroSection';
import PhotoGallery from '../components/PhotoGallery';
import Footer from '../components/Footer';
import '../styles/HomePage.css';

const HomePage = () => {
  const navigate = useNavigate();

  const menuItems = [
    { title: 'Home', path: '/' },
    { title: 'Store', path: '/store' },
    { title: 'Cart', path: '/cart' },
    { title: 'J2E', path: '/j2e' },
    { title: 'Search Engine', path: '/search' },
    { title: 'Align With Us', path: '/align' },
    { title: 'Vibrational Intelligence', path: '/vibrational-intelligence' },
    { title: 'The Journey', path: '/the-journey' },
    { title: 'Podcast/Vlog', path: '/podcast' },
    { title: 'Q', path: '/chatbot' },
    { title: 'Contact Us', path: '/contact' },
  ];

  const featureBoxes = [
    {
      title: 'Sea Moss',
      image: '/images/gold-gel.jpg',
      path: '/seamoss'
    },
    {
      title: 'J2E',
      image: '/images/J2EPod.jpg',
      path: '/j2e'
    },
    {
      title: 'Castor Oil',
      image: '/images/castor-oil.jpg',
      path: '/oils'
    },
    {
      title: 'Vibrational Intelligence',
      image: '/images/DNA.jpg',
      path: '/vibrational-intelligence'
    },
    {
      title: 'Recipes',
      image: '/images/recipes.jpg',
      path: '/recipes'
    },
    {
      title: 'Positive Vibes',
      image: '/images/music.jpg',
      path: '/positive-vibes'
    }
  ];

  return (
    <div className="homepage-container">

      {/* ðŸŸ¦ NAV MENU (replaces Navbar.js) */}
      <div className="menu-grid multicolor-border">
        {menuItems.map((item, index) => (
          <div key={index} className="menu-box" onClick={() => navigate(item.path)}>
            {item.title}
          </div>
        ))}
      </div>

      {/* ðŸŸ§ HERO TITLE */}
      <div className="multicolor-border hero-container-title">
        <h1 className="hero-title">Home</h1>
      </div>

      {/* ðŸŸ¥ LOGO SECTION with black background */}
      <div className="logo-section multicolor-border">
        <img src="/images/MAVJLogo.png" alt="MAVJ Logo" className="logo-image" />
      </div>

      {/* ðŸŸ¨ PARAGRAPH */}
      <div className="intro-paragraph multicolor-border">
        <p>
          We are a movement guided by frequency, natural rhythms, and ancestral memory.
          Our mission is to restore balance and vitality through vibrationally harvested
          Sea Moss, sacred Castor Oils, and the immersive Human Frequency Transformation
          experience in St. Lucia.
        </p>
      </div>

      {/* ðŸŸ© FEATURE BOXES GRID */}
      <div className="feature-grid multicolor-border">
        {featureBoxes.map((box, i) => (
          <div key={i} className="feature-box multicolor-border" onClick={() => navigate(box.path)}>
            <img src={box.image} alt={box.title} className="feature-image" />
            <h3>{box.title}</h3>
          </div>
        ))}
      </div>

      {/* ðŸŸ« GALLERY */}
      <PhotoGallery />

      {/* â¬› FOOTER */}
      <Footer />
    </div>
  );
};

export default HomePage;
