// src/Pages/HomePage.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/HomePage.css';
import MAVJLogo from '../components/MAVJLogo';

const HomePage = () => {
  const navigate = useNavigate();

  const featureBoxes = [
    { title: 'Sea Moss', image: '/images/gold-gel.jpg', path: '/seamoss' },
    { title: 'Castor Oil', image: '/images/castor-oil.jpg', path: '/oils' },
    { title: 'J2E 7-Day', image: '/images/J2EPod.jpg', path: '/j2e' },
    { title: 'Biochemistry Cards', image: '/images/biochem.jpg', path: '/science' },
    { title: 'Glossary', image: '/images/glossary.jpg', path: '/science' },
    { title: 'Body Map', image: '/images/bodymap.jpg', path: '/science' }
  ];

  return (
    <div className="bg-black text-gold min-h-screen">
      {/* ðŸŸ¦ Hero Section Title */}
      <div className="w-full multicolor-border p-4 mb-4">
        <h1 className="text-4xl font-bold text-center">Home</h1>
      </div>

      {/* ðŸŸ¥ Logo and Video Container */}
      <div className="w-full multicolor-border p-6 flex justify-center items-center bg-black mb-4">
        <MAVJLogo />
      </div>

      {/* ðŸŸ¨ Paragraph Container */}
      <div className="w-full multicolor-border p-6 bg-black mb-4">
        <p className="text-lg text-center">
          We are a movement guided by frequency, natural rhythms, and ancestral memory. Our mission is to restore balance and vitality through vibrationally harvested Sea Moss, sacred Castor Oils, and the immersive Human Frequency Transformation experience in St. Lucia.
        </p>
      </div>

      {/* ðŸŸ© Feature Content Boxes 3x2 */}
      <div className="w-full multicolor-border p-6 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4 bg-black mb-4">
        {featureBoxes.map((box, i) => (
          <div
            key={i}
            onClick={() => navigate(box.path)}
            className="cursor-pointer multicolor-border p-4 flex flex-col items-center justify-center hover:shadow-lg hover:scale-105 transition-transform"
          >
            <img src={box.image} alt={box.title} className="w-full h-40 object-cover mb-2" />
            <h3 className="text-xl font-semibold text-center">{box.title}</h3>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
