// src/components/PageLayout.jsx
import React from 'react';
import Navbar from './Navbar';
import HeroSection from './HeroSection';
import PhotoGallery from './PhotoGallery';
import Footer from './Footer';

const PageLayout = ({
  title,
  subtitle,
  showHero = true,
  showFooter = true,
  showGallery = true,
  children,
}) => {
  return (
    <div className="page-layout">
      {/* ðŸŸ¦ Container 1: Navbar */}
      <div className="multicolor-border navbar-container">
        <Navbar />
      </div>

      {/* ðŸŸ¨ Container 2: Hero Section */}
      {showHero && (
        <div className="multicolor-border hero-section-container">
          <HeroSection title={title} subtitle={subtitle} />
        </div>
      )}

      {/* ðŸŸª Container 3: Logo with Video Background */}
      <div className="multicolor-border logo-video-container">
        <video autoPlay muted loop playsInline className="hero-video">
          <source src="/videos/Nubian.mp4" type="video/mp4" />
        </video>
        <div className="hero-overlay">
          <img
            src="https://raw.githubusercontent.com/MyAlkalineVeganJourney/HF/main/Images/MAVJDigitalLogo.JPG"
            alt="MAVJ Logo"
            className="MAVJlogo"
          />
        </div>
      </div>

      {/* ðŸŸ§ Container 4: Intro Paragraph */}
      <div className="multicolor-border intro-paragraph">
        <p>
          Welcome to My Alkaline Vegan Journey â€” a holistic health platform rooted in vibrational healing,
          nutrition, and global connection. Here, ancient wisdom meets modern science to guide your journey
          through self-realization, nourishment, and enlightenment.
        </p>
      </div>

      {/* ðŸŸ¨ Container 5: Content Boxes */}
      <div className="multicolor-border feature-grid">
        {[1,2,3,4,5,6].map((_, index) => (
          <div key={index} className="feature-box glowing-hover">
            <img src={`/images/banner${index + 1}.jpg`} alt={`Box ${index + 1}`} className="content-image" />
            <h3 className="content-title">Box {index + 1}</h3>
          </div>
        ))}
      </div>

      {/* ðŸŸ¥ Container 6: Photo Gallery */}
      {showGallery && (
        <div className="multicolor-border">
          <PhotoGallery />
        </div>
      )}

      {/* ðŸŸ© Container 7: Footer */}
      {showFooter && (
        <div className="multicolor-border">
          <Footer />
        </div>
      )}
    </div>
  );
};

export default PageLayout;
