// src/Pages/HomePage.jsx

import React from 'react';
import { useNavigate } from 'react-router-dom';
import HeroSection from '../components/HeroSection';
import PageLayout from '../components/PageLayout';
import InfoCard from '../components/InfoCard';
import '../styles/HomePage.css'; // CSS for homepage styles

const HomePage = () => {
  const navigate = useNavigate();

  const sections = [
    {
      title: 'Journey To Enlightenment',
      image: '/images/J2EPod.jpg',
      description: 'Experience our 7-day transformation retreat in Saint Lucia.',
      path: '/j2e'
    },
    {
      title: 'Vibrational Intelligence',
      image: '/images/DNA.jpg',
      description: 'Explore the science of frequency, alchemy, and vitality.',
      path: '/vibrational-intelligence'
    },
    {
      title: 'Podcast',
      image: '/images/podcast.jpg',
      description: 'Elevate your mindset through our conscious conversations.',
      path: '/podcast'
    },
    {
      title: 'Chatbot - Q',
      image: '/images/ai-chat.jpg',
      description: 'Ask Q anything about our products, events, or philosophy.',
      path: '/chatbot'
    },
    {
      title: 'Search',
      image: '/images/search.jpg',
      description: 'Look up herbs, recipes, and holistic solutions by need.',
      path: '/search'
    },
    {
      title: 'Align With Us',
      image: '/images/align.jpg',
      description: 'Partner, collaborate, or support the mission.',
      path: '/align'
    }
  ];

  return (
    <>
      <HeroSection
        title="ðŸŒ± My Alkaline Vegan Journey"
        subtitle="Reset. Rise. Resonate. Resurrect."
        background="/videos/landing-banner.mp4" // Or replace with "/images/my-logo.jpg"
      />

      <PageLayout title="Featured Sections" subtitle="Explore the journey below.">
        <div className="info-grid">
          {sections.map((sec, i) => (
            <InfoCard
              key={i}
              title={sec.title}
              image={sec.image}
              description={sec.description}
              onClick={() => navigate(sec.path)}
            />
          ))}
        </div>
      </PageLayout>
    </>
  );
};

export default HomePage;
