// src/components/PageLayout.jsx
import React from 'react';
import Navbar from './Navbar';
import Footer from './Footer';
import '../styles/PageLayout.css';

const PageLayout = ({ title, subtitle, paragraph, children }) => {
  return (
    <div className="page-layout">
      {/* ðŸŸ© Container 1: Navbar */}
      <div className="multicolor-border sticky-top">
        <Navbar />
      </div>

      {/* ðŸŸ¨ Container 2: Page Title */}
      <div className="page-title-container multicolor-border sticky-top">
        <img src="MAVJLogo" className="page-logo" />
        {title && <h1 className="page-title">{title}</h1>}
        {subtitle && <p className="page-subtitle">{subtitle}</p>}
      </div>

      {/* ðŸŸ¦ Container 3: Hero Section */}
      <div className="hero-section multicolor-border">
        <video autoPlay muted loop playsInline className="hero-video">
          <source src="/videos/Nubian.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
        <div className="hero-overlay">
          <img src="/images/MAVJLogo.png" alt="MAVJ Logo" className="hero-logo" />
        </div>
      </div>

      {/* ðŸŸ§ Container 4: Intro Paragraph */}
      <div className="intro-paragraph multicolor-border">
        {paragraph && <p>{paragraph}</p>}
      </div>

      {/* ðŸŸª Container 5: Page Content */}
      <div className="page-content multicolor-border">
        <div className="grid-layout">
          {children}
        </div>
      </div>

      {/* ðŸŸ« Container 6: Gallery */}
      <div className="gallery-container multicolor-border">
        <h2>ðŸ“¸ MAVJ Photo Gallery</h2>
        <p>Gallery coming soon...</p>
      </div>

      {/* ðŸŸ¥ Container 7: Footer */}
      <Footer />
    </div>
  );
};

export default PageLayout;
