// src/Pages/HomePage.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/HomePage.css';
import PhotoGallery from '../components/PhotoGallery';
import Footer from '../components/Footer';
import MAVJLogo from '../components/MAVJLogo';

const HomePage = () => {
  const navigate = useNavigate();

  const featuredItems = [
    {
      title: 'Sea Moss',
      image: '/images/gold-gel.jpg',
      path: '/seamoss'
    },
    {
      title: 'Castor Oil',
      image: '/images/castor-oil.jpg',
      path: '/oils'
    },
    {
      title: 'J2E Transformation',
      image: '/images/J2EPod.jpg',
      path: '/j2e'
    },
    {
      title: 'Biochemistry Cards',
      image: '/images/biochem.jpg',
      path: '/biochemistry'
    },
    {
      title: 'Glossary',
      image: '/images/glossary.jpg',
      path: '/glossary'
    },
    {
      title: 'Body System Map',
      image: '/images/bodymap.jpg',
      path: '/bodymap'
    }
  ];

  return (
    <div className="homepage black-background text-gold">

      {/* ðŸŸ¦ PAGE TITLE */}
      <div className="container multicolor-border full-width">
        <h1 className="hero-title">Home</h1>
      </div>

      {/* ðŸŸ¥ LOGO + VIDEO BACKGROUND */}
      <div className="container multicolor-border full-width logo-section">
        <video autoPlay muted loop className="background-video">
          <source src="/videos/homepage-bg.mp4" type="video/mp4" />
        </video>
        <div className="video-overlay">
          <MAVJLogo />
        </div>
      </div>

      {/* ðŸŸ¨ INTRO PARAGRAPH */}
      <div className="container multicolor-border full-width">
        <p>
          We are a movement guided by frequency, natural rhythms, and ancestral memory.
          Our mission is to restore balance and vitality through vibrationally harvested
          Sea Moss, sacred Castor Oils, and the immersive Human Frequency Transformation
          experience in St. Lucia.
        </p>
      </div>

      {/* ðŸŸ© SIX CONTENT BOXES */}
      <div className="container multicolor-border full-width feature-grid">
        {featuredItems.map((item, i) => (
          <div
            key={i}
            className="feature-box multicolor-border glow-hover"
            onClick={() => navigate(item.path)}
          >
            <img src={item.image} alt={item.title} className="feature-image" />
            <h3>{item.title}</h3>
          </div>
        ))}
      </div>

      {/* â¬› GALLERY */}
      <div className="container multicolor-border full-width">
        <PhotoGallery />
      </div>

      {/* â¬› FOOTER */}
      <div className="container multicolor-border full-width">
        <Footer />
      </div>
    </div>
  );
};

export default HomePage;
