import React from 'react';
import HeroSection from '../../components/HeroSection';
import PageLayout from '../../components/PageLayout';
import InfoCard from '../../components/InfoCard';

const Chakras = () => {
  return (
    <PageLayout>
      <HeroSection
        title="Chakras"
        subtitle="Energy Centers of the Body"
        background="/videos/chakras-hero.mp4"
        isVideo={true}
      />

      <InfoCard
        title="What Are Chakras?"
        text="The chakra system is an ancient energy framework originating in India. It describes seven main energy centers aligned along the spine, each connected to specific physical, emotional, and spiritual functions."
      />

      <section className="max-w-4xl mx-auto my-10 p-6 bg-gray-800 rounded-xl shadow-lg">
        <h2 className="text-2xl font-bold mb-4 text-white">Core Chakra Functions</h2>
        <p className="text-gray-300 mb-4">
          For example, the <strong>root chakra</strong> relates to grounding and survival, while the <strong>crown chakra</strong> governs higher consciousness. Each chakra has a color, sound frequency, and endocrine gland itâ€™s linked to.
        </p>
        <p className="text-gray-300 mb-4">
          Balanced chakras promote holistic well-being. Imbalances can manifest in emotional distress or physical illness. Practices like meditation, sound healing, and breathwork help realign them.
        </p>
      </section>

      <section className="max-w-5xl mx-auto my-16">
        <h2 className="text-2xl md:text-3xl font-semibold mb-6 text-white">7 Major Chakras Overview</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {[
            {
              name: 'Root (Muladhara)',
              color: 'Red',
              function: 'Survival, grounding, safety',
            },
            {
              name: 'Sacral (Svadhisthana)',
              color: 'Orange',
              function: 'Creativity, sexuality, emotions',
            },
            {
              name: 'Solar Plexus (Manipura)',
              color: 'Yellow',
              function: 'Power, will, digestion',
            },
            {
              name: 'Heart (Anahata)',
              color: 'Green',
              function: 'Love, compassion, connection',
            },
            {
              name: 'Throat (Vishuddha)',
              color: 'Blue',
              function: 'Communication, expression, truth',
            },
            {
              name: 'Third Eye (Ajna)',
              color: 'Indigo',
              function: 'Intuition, insight, vision',
            },
            {
              name: 'Crown (Sahasrara)',
              color: 'Violet or White',
              function: 'Spirituality, unity, higher states of consciousness',
            },
          ].map((chakra, idx) => (
            <div
              key={idx}
              className="bg-gray-800 p-5 rounded-xl shadow-md text-white"
            >
              <h3 className="text-xl font-semibold mb-2">{chakra.name}</h3>
              <p><strong>Color:</strong> {chakra.color}</p>
              <p><strong>Function:</strong> {chakra.function}</p>
            </div>
          ))}
        </div>
      </section>
    </PageLayout>
  );
};

export default Chakras;
