// src/components/PageLayout.jsx

import React from 'react';
import Navbar from './Navbar';
import HeroSection from './HeroSection';
import Footer from './Footer';
import PhotoGallery from './PhotoGallery';
import '../styles/layout.css'; // optional: for scoped layout styling

const PageLayout = ({ title = '', subtitle = '', children }) => {
  return (
    <div className="bg-black text-gold min-h-screen">
      {/* Container 1: Navigation */}
      <Navbar />

      {/* Container 2: Hero Section */}
      <div className="multicolor-border p-6 mb-6">
        <HeroSection title={title} subtitle={subtitle} />
      </div>

      {/* Container 3: Dynamic Content Area */}
      <div className="multicolor-border p-6 mb-6">
        {children}
      </div>

      {/* Container 4: Photo Gallery */}
      <div className="multicolor-border p-6 mb-6">
        <PhotoGallery />
      </div>

      {/* Container 5: Footer */}
      <div className="multicolor-border p-6">
        <Footer />
      </div>
    </div>
  );
};

export default PageLayout;
