// src/pages/Seeds.jsx
import React from 'react';
import PageLayout from '../components/PageLayout';
import InfoCard from '../components/InfoCard';
import seedsData from '../data/SeedsData'; // Make sure this path matches your file structure
import '../styles/info.css';

const Seeds = () => {
  return (
    <PageLayout title="üå± Organic Heirloom Seeds">
      <div className="info-page-container">
        <section className="multicolor-border">
          <h2>üåç Earth-Based Sovereignty</h2>
          <p>
            All seeds offered through MAVJ are non-GMO, wild harvested or heirloom varieties used for generations by
            indigenous communities. These are seeds of transformation‚Äîphysically and energetically.
          </p>
        </section>

        <section className="multicolor-border">
          <h2>üß¨ Offered Seasonally</h2>
          <p>
            We offer herbs, fruits, and vegetables with medicinal and regenerative benefits. Join our seasonal
            newsletter to be notified when the next seed collection opens.
          </p>
        </section>

        {/* Grid of InfoCards for all seeds */}
        <div className="grid-container">
          {seedsData.map((seed) => (
            <InfoCard key={seed.id} data={seed} />
          ))}
        </div>
      </div>
    </PageLayout>
  );
};

export default Seeds;
