// src/Pages/HomePage.jsx
import React from 'react';
import PageLayout from '../components/PageLayout';
import { Link } from 'react-router-dom';

const contentBoxes = [
  {
    title: '💫 Journey to Enlightenment',
    image: '/images/J2EBanner.jpg',
    link: '/j2e'
  },
  {
    title: '💎 Sea Moss',
    image: '/images/SeaMossBanner.jpg',
    link: '/seamoss'
  },
  {
    title: '🌑 Castor Oil',
    image: '/images/CastorOilBanner.jpg',
    link: '/oils'
  },
  {
    title: '⚛️ Vibrational Intelligence',
    image: '/images/VibrationIntelligence.jpg',
    link: '/VibrationalIntelligence'
  },
  {
    title: '🎉 Align With Us',
    image: '/images/AlignBanner.jpg',
    link: '/align'
  },
  {
    title: '🍽️ Let’s Eat (Recipes)',
    image: '/images/RecipesBanner.jpg',
    link: '/recipes'
  }
];

const HomePage = () => {
  return (
    <PageLayout
      title="💦 MAVJ Home 💦"
      subtitle="Your journey to health and enlightenment begins here"
      showHero={true}
      showFooter={true}
    >
      {/* Intro Paragraph */}
      <section className="intro-paragraph">
        <p>
          Welcome to My Alkaline Vegan Journey — a holistic health platform rooted in vibrational healing, nutrition, and global connection. Here, ancient wisdom meets modern science to guide your journey through self-realization, nourishment, and enlightenment.
        </p>
      </section>

      {/* Content Boxes Grid */}
      <section className="feature-grid">
        {contentBoxes.map((box, index) => (
          <Link to={box.link} key={index} className="feature-box">
            <img
              src={box.image}
              alt={box.title}
              className="feature-image"
            />
            <h3>{box.title}</h3>
          </Link>
        ))}
      </section>

      {/* Future Photo Gallery Section */}
      <section className="multicolor-border" style={{ marginTop: '60px', textAlign: 'center' }}>
        <h2>📸 Photo Gallery</h2>
        <p>Gallery content coming soon...</p>
      </section>
    </PageLayout>
  );
};

export default HomePage;
