// HeroSection.jsx
import React from 'react';
import './HeroSection.css';

const HeroSection = ({ title, subtitle, background, isVideo = false }) => {
  const backgroundStyle = isVideo
    ? {}
    : { backgroundImage: `url(${process.env.PUBLIC_URL + background})` }; // üëàüèΩ Fix path

  return (
    <div className="hero" style={backgroundStyle}>
      {isVideo ? (
        <video autoPlay muted loop className="absolute top-0 left-0 w-full h-full object-cover">
          <source src={process.env.PUBLIC_URL + background} type="video/mp4" /> {/* üëàüèΩ Fix video path */}
        </video>
      ) : null}
      <div className="hero-content relative z-10">
        <h1 className="text-4xl md:text-6xl font-bold mb-4">{title}</h1>
        {subtitle && <p className="text-xl">{subtitle}</p>}
      </div>
    </div>
  );
};

export default HeroSection;
