// src/components/PageLayout.jsx
import React from 'react';
import HeroSection from './HeroSection';
import Footer from './Footer';
import PhotoGallery from './PhotoGallery';
import Navbar from './Navbar';

const PageLayout = ({
  title,
  subtitle,
  showHero = true,
  showGallery = true,
  showFooter = true,
  children,
}) => {
  return (
    <div className="page-layout">
      {/* Container 1: Fixed Navbar */}
      <div className="container navbar-container multicolor-border">
        <Navbar />
      </div>

      {/* Container 2: Title (Hero Section) */}
      {showHero && (
        <div className="container hero-container multicolor-border">
          <HeroSection title={title} subtitle={subtitle} />
        </div>
      )}

      {/* Container 3: Logo Section */}
      <div className="container logo-section multicolor-border">
        <video autoPlay muted loop playsInline className="logo-video-background">
          <source src="/videos/Nubian.mp4" type="video/mp4" />
        </video>
        <img
          src="/images/MAVJLogo.jpg"
          alt="MAVJ Logo"
          className="logo-image"
        />
      </div>

      {/* Container 4: Page Paragraph */}
      <div className="container intro-paragraph multicolor-border">
        <p>
          Welcome to My Alkaline Vegan Journey — where our mission is rooted in natural healing,
          spiritual growth, and community alignment. Discover resources, engage in enlightening content,
          and align your lifestyle with the vibrational frequency of wellness.
        </p>
      </div>

      {/* Container 5: Six Content Boxes */}
      <div className="container feature-grid multicolor-border">
        <div className="feature-box">
          <img src="/images/J2EBanner.jpg" alt="J2E" className="feature-image" />
          <h3>Journey2Enlightenment</h3>
        </div>
        <div className="feature-box">
          <img src="/images/SeaMossBanner.jpg" alt="Sea Moss" className="feature-image" />
          <h3>MAVJSeaMoss</h3>
        </div>
        <div className="feature-box">
          <img src="/images/CastorOilBanner.jpg" alt="Castor Oil" className="feature-image" />
          <h3>SolarLunarCastorOil</h3>
        </div>
        <div className="feature-box">
          <img src="/images/PodcastBanner.jpg" alt="Podcast" className="feature-image" />
          <h3>Podcast / Vlog</h3>
        </div>
        <div className="feature-box">
          <img src="/images/LiveStreamBanner.jpg" alt="Live Broadcast" className="feature-image" />
          <h3>Live Broadcast</h3>
        </div>
        <div className="feature-box">
          <img src="/images/StoreBanner.jpg" alt="MAVJ Store" className="feature-image" />
          <h3>MAVJStore</h3>
        </div>
      </div>

      {/* Container 6: Photo Gallery */}
      {showGallery && (
        <div className="container photo-gallery-container multicolor-border">
          <PhotoGallery />
        </div>
      )}

      {/* Container 7: Footer */}
      {showFooter && (
        <div className="container footer-container multicolor-border">
          <Footer />
        </div>
      )}
    </div>
  );
};

export default PageLayout;
