// src/components/PageLayout.jsx
import React from 'react';
import HeroSection from './HeroSection';
import Footer from './Footer';
import PhotoGallery from './PhotoGallery';
import Navbar from './Navbar'; // ✅ Add this for consistency

const PageLayout = ({
  title,
  subtitle,
  showHero = true,
  showGallery = true,
  showFooter = true,
  children,
}) => {
  return (
    <div className="page-layout bg-black text-gold min-h-screen">
      <Navbar /> {/* ✅ Now always visible */}
      {showHero && <HeroSection title={title} subtitle={subtitle} />}
      <main className="content-container">{children}</main>
      {showGallery && <PhotoGallery />}
      {showFooter && <Footer />}
    </div>
  );
};

export default PageLayout;
