// src/components/PageLayout.jsx
import React from 'react';
import Navbar from './Navbar';
import HeroSection from './HeroSection';
import PhotoGallery from './PhotoGallery';
import Footer from './Footer';

const PageLayout = ({
  title,
  subtitle,
  showHero = true,
  showFooter = true,
  showGallery = true,
  children,
}) => {
  return (
    <div className="page-layout">
      {/* ðŸŸ¦ Container 1: Navbar */}
      <div className="multicolor-border">
        <Navbar />
      </div>

      {/* ðŸŸ¨ Container 2: Page Title (Hero Section) */}
      {showHero && (
        <div className="multicolor-border">
          <HeroSection title={title} subtitle={subtitle} />
        </div>
      )}

      {/* ðŸŸ« Container 3: Page Content */}
      <div className="multicolor-border page-content">{children}</div>

      {/* ðŸŸ¥ Container 6: Photo Gallery */}
      {showGallery && (
        <div className="multicolor-border">
          <PhotoGallery />
        </div>
      )}

      {/* ðŸŸ© Container 7: Footer */}
      {showFooter && (
        <div className="multicolor-border">
          <Footer />
        </div>
      )}
    </div>
  );
};

export default PageLayout;
