// src/Pages/HomePage.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/HomePage.css';
import MAVJLogo from '../components/MAVJLogo';

const HomePage = () => {
  const navigate = useNavigate();

  const featureBoxes = [
    { title: 'Sea Moss', image: '/images/SeaMossBanner.jpg', path: '/seamoss' },
    { title: 'Castor Oil', image: '/images/CastorOil.jpg', path: '/oils' },
    { title: 'J2E 7-Day', image: '/images/J2EPod.jpg', path: '/j2e' },
    { title: 'Biochemistry Cards', image: '/images/Atom.jpg', path: '/science' },
    { title: 'Glossary', image: '/images/DNA.jpg', path: '/science' },
    { title: 'Body Map', image: '/images/BodyMap.jpg', path: '/science' }
  ];

  return (
    <div className="bg-black text-gold min-h-screen w-full">
      {/* ðŸŸ¦ Page Title Container */}
      <div className="w-full multicolor-border p-6 mb-6">
        <h1 className="text-4xl font-bold text-center text-gold">Home</h1>
      </div>

      {/* ðŸŸ¥ Logo and Video Container */}
      <div className="w-full multicolor-border p-6 flex justify-center items-center bg-black mb-6">
        <MAVJLogo />
      </div>

      {/* ðŸŸ¨ Intro Paragraph Container */}
      <div className="w-full multicolor-border p-6 bg-black mb-6">
        <p className="text-lg text-center text-gold max-w-4xl mx-auto">
          We are a movement guided by frequency, natural rhythms, and ancestral memory. Our mission is to restore balance and vitality through vibrationally harvested Sea Moss, sacred Castor Oils, and the immersive Human Frequency Transformation experience in St. Lucia.
        </p>
      </div>

      {/* ðŸŸ© Six Content Boxes Container - 3 Columns x 2 Rows */}
      <div className="w-full multicolor-border p-6 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 bg-black mb-6">
        {featureBoxes.map((box, i) => (
          <div
            key={i}
            onClick={() => navigate(box.path)}
            className="feature-box multicolor-border p-4 flex flex-col items-center justify-center hover:shadow-lg transition-transform"
          >
            <img
              src={box.image}
              alt={box.title}
              className="w-full h-48 object-contain mb-2 rounded-lg"
            />
            <h3 className="text-xl font-semibold text-center text-gold">{box.title}</h3>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
