import React from 'react';
import Navbar from './Navbar';
import Footer from './Footer';
import '../styles/PageLayout.css';

const PageLayout = ({ title, subtitle, children }) => {
  return (
    <div className="page-layout">
      {/* Container 1: Navbar */}
      <Navbar />

      {/* Container 2: Page Title (Hero) */}
      <header className="hero-section multicolor-border">
        <div className="hero-overlay">
          <img src="/images/MAVJLogo.png" alt="MAVJ Logo" className="hero-logo" />
          {title && <h1 className="hero-title">{title}</h1>}
          {subtitle && <p className="hero-subtitle">{subtitle}</p>}
        </div>
      </header>

      {/* Container 3: Logo Centered Again (if desired separately) */}
      <div className="container-three multicolor-border">
        <img src="/images/MAVJLogo.png" alt="Centered Logo" className="hero-logo" />
      </div>

      {/* Container 4: Paragraph or Intro */}
      <div className="intro-paragraph">
        <p>Insert your main introductory paragraph here. This is usually a welcome or overview message.</p>
      </div>

      {/* Container 5: Six Content Boxes */}
      <div className="feature-grid multicolor-border">
        {children}
      </div>

      {/* Container 6: Photo Gallery Placeholder */}
      <div className="photo-gallery multicolor-border">
        <h2>ðŸ“¸ MAVJ Photo Gallery</h2>
        {/* Photo components go here */}
      </div>

      {/* Container 7: Footer */}
      <Footer />
    </div>
  );
};

export default PageLayout;
