// src/pages/HomePage.jsx
import React from 'react';
import PageLayout from '../components/PageLayout';
import { Link } from 'react-router-dom';

const contentBoxes = [
  {
    title: '💫 Journey to Enlightenment',
    image: '/images/J2EBanner.jpg',
    link: '/j2e'
  },
  {
    title: '💎 Sea Moss',
    image: '/images/SeaMossBanner.jpg',
    link: '/seamoss'
  },
  {
    title: '🌑 Castor Oil',
    image: '/images/CastorOilBanner.jpg',
    link: '/oils'
  },
  {
    title: '⚛️ Vibrational Intelligence',
    image: '/images/VibrationIntelligence.jpg',
    link: '/VibrationalIntelligence'
  },
  {
    title: '🎉 Align With Us',
    image: '/images/AlignBanner.jpg',
    link: '/align'
  },
  {
    title: '🍽️ Let’s Eat (Recipes)',
    image: '/images/RecipesBanner.jpg',
    link: '/recipes'
  }
];

const HomePage = () => {
  return (
    <PageLayout
      title="💦 MAVJ Home 💦"
      subtitle="Holistic Wellness & Vibrational Healing"
      showHero={true}
      showFooter={true}
    >
      {/* Container 3: Centered Logo over Black Background + Video */}
      <section className="hero-section multicolor-border">
        <video autoPlay muted loop playsInline className="hero-video">
          <source src="/videos/Nubian.mp4" type="video/mp4" />
        </video>
        <div className="hero-overlay">
          <img
            src="https://raw.githubusercontent.com/MyAlkalineVeganJourney/HF/main/Images/MAVJDigitalLogo.JPG"
            alt="MAVJ Logo"
          />
        </div>
      </section>

      {/* Container 4: Page Paragraph */}
      <section className="intro-paragraph multicolor-border">
        <p>
          Welcome to My Alkaline Vegan Journey — a holistic health platform rooted in vibrational healing,
          nutrition, and global connection. Here, ancient wisdom meets modern science to guide your journey through
          self-realization, nourishment, and enlightenment.
        </p>
      </section>

      {/* Container 5: Content Boxes */}
      <section className="multicolor-border">
        <div className="feature-grid">
          {contentBoxes.map((box, index) => (
            <Link to={box.link} key={index} className="feature-box glowing-hover">
              <img src={box.image} alt={box.title} />
              <h3>{box.title}</h3>
            </Link>
          ))}
        </div>
      </section>

      {/* Container 6: Photo Gallery Placeholder */}
      <section className="multicolor-border photo-gallery">
        <h2 className="text-center">📸 Photo Gallery Coming Soon</h2>
      </section>
    </PageLayout>
  );
};

export default HomePage;
