// src/Pages/HomePage.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/HomePage.css';

const HomePage = () => {
  const navigate = useNavigate();

  const menuItems = [
    'Home', 'Store', 'Cart', 'J2E', 'Search Engine', 'Align With Us',
    'Vibrational Intelligence', 'The Journey', 'Podcast/Vlog', 'Q', 'Contact Us', 'Live Broadcast'
  ];

  const featuredItems = [
    {
      title: 'Sea Moss',
      image: '/images/gold-gel.jpg',
      path: '/seamoss'
    },
    {
      title: 'Black Castor Oil',
      image: '/images/castor-oil.jpg',
      path: '/oils'
    },
    {
      title: 'J2E 7-Day St. Lucia',
      image: '/images/J2EPod.jpg',
      path: '/j2e'
    },
    {
      title: 'Biochemistry Cards',
      image: '/images/biochem.jpg',
      path: '/science'
    },
    {
      title: 'Glossary of Compounds',
      image: '/images/glossary.jpg',
      path: '/science'
    },
    {
      title: 'Body Systems Map',
      image: '/images/bodymap.jpg',
      path: '/science'
    }
  ];

  return (
    <div className="homepage-wrapper bg-black text-gold">

      {/* 1. NAVIGATION MENU (Multicolored Border Grid) */}
      <div className="container multicolor-border menu-grid">
        {menuItems.map((item, index) => (
          <div
            key={index}
            className="menu-box multicolor-border hover-glow"
            onClick={() => navigate(`/${item.toLowerCase().replace(/ /g, '-')}`)}>
            {item}
          </div>
        ))}
      </div>

      {/* 2. PAGE TITLE */}
      <div className="container multicolor-border hero-title-section">
        <h1 className="text-4xl font-bold text-center">Home</h1>
      </div>

      {/* 3. LOGO WITH VIDEO BACKGROUND SUPPORT */}
      <div className="container multicolor-border logo-video-section relative">
        <video autoPlay muted loop playsInline className="background-video">
          <source src="/videos/home-background.mp4" type="video/mp4" />
        </video>
        <div className="overlay-content">
          <img src="/images/MAVJLogo.png" alt="MAVJ Logo" className="logo-centered" />
        </div>
      </div>

      {/* 4. INTRO PARAGRAPH */}
      <div className="container multicolor-border paragraph-section">
        <p className="text-lg text-center">
          We are a movement guided by frequency, natural rhythms, and ancestral memory.
          Our mission is to restore balance and vitality through vibrationally harvested
          Sea Moss, sacred Castor Oils, and the immersive Human Frequency Transformation
          experience in St. Lucia.
        </p>
      </div>

      {/* 5. FEATURED CONTENT BOXES */}
      <div className="container multicolor-border content-boxes-grid">
        {featuredItems.map((item, i) => (
          <div key={i} className="content-box multicolor-border hover-glow" onClick={() => navigate(item.path)}>
            <img src={item.image} alt={item.title} className="content-image" />
            <h3 className="text-center mt-2">{item.title}</h3>
          </div>
        ))}
      </div>

      {/* 6. PHOTO GALLERY */}
      <div className="container multicolor-border">
        <p className="text-center italic text-sm">[Photo Gallery Placeholder]</p>
        {/* Replace with <PhotoGallery /> once fully implemented */}
      </div>

      {/* 7. FOOTER */}
      <div className="container multicolor-border">
        <p className="text-center text-sm">[Footer Placeholder]</p>
        {/* Replace with <Footer /> once fully implemented */}
      </div>

    </div>
  );
};

export default HomePage;
