// src/Pages/HomePage.jsx
import React from 'react';
import PageLayout from '../components/PageLayout';
import { Link } from 'react-router-dom';

const contentBoxes = [
  {
    title: '💫 Journey to Enlightenment',
    image: '/images/J2EBanner.jpg',
    link: '/j2e'
  },
  {
    title: '💎 Sea Moss',
    image: '/images/SeaMossBanner.jpg',
    link: '/seamoss'
  },
  {
    title: '🌑 Castor Oil',
    image: '/images/CastorOilBanner.jpg',
    link: '/oils'
  },
  {
    title: '⚛️ Vibrational Intelligence',
    image: '/images/VibrationIntelligence.jpg',
    link: '/VibrationalIntelligence'
  },
  {
    title: '🎉 Align With Us',
    image: '/images/AlignBanner.jpg',
    link: '/align'
  },
  {
    title: '🍽️ Let’s Eat (Recipes)',
    image: '/images/RecipesBanner.jpg',
    link: '/recipes'
  }
];

const HomePage = () => {
  return (
    <PageLayout
      title="💦 MAVJ Home 💦"
      showHero={true}
      showGallery={false}
      showFooter={false}
    >
      {/* Intro paragraph section (Container 4) */}
      <section className="intro-paragraph multicolor-border">
        <p>
          Welcome to My Alkaline Vegan Journey — a holistic health platform rooted in vibrational healing, nutrition, and global connection. Here, ancient wisdom meets modern science to guide your journey through self-realization, nourishment, and enlightenment.
        </p>
      </section>

      {/* Feature Content Boxes (Container 5) */}
      <section className="feature-container multicolor-border">
        <div className="feature-grid">
          {contentBoxes.map((box, index) => (
            <Link to={box.link} key={index} className="feature-box glowing-hover">
              <img
                src={box.image}
                alt={box.title}
                className="feature-image"
                style={{ height: '140px', objectFit: 'cover', width: '100%', borderRadius: '8px' }}
              />
              <h3>{box.title}</h3>
            </Link>
          ))}
        </div>
      </section>
    </PageLayout>
  );
};

export default HomePage;
